import React, { useState } from "react";
import { emojis, users, commands, channels } from "../../data/data";
import { FaPen, FaCheck } from "react-icons/fa";
import Chart from "../charts/chart";
import {
  onMD,
  onML,
  onMM,
  onMU,
  handleTouchStart,
  handleTouchMove,
  handleTouchEnd,
} from "./events";
const Dashboard = () => {
  const [move, setMove] = useState([0, 0]);
  const [prefix, setPrefix] = useState("!");
  const [edit, setEdit] = useState(false);

  return (
    <div
      className="container-fluid pb-3"
      style={{ maxWidth: "1300px", minHeight: "100%" }}
    >
      <div className="row no-gutters justify-content-between">
        <div
          className="p-2 col-lg-4 col-md-8 col-sm-8 col-12"
          style={{ height: "300px" }}
        >
          <div className="pnl convex-1 row px-4 py-2 no-gutters justify-content-between newish h-100">
            <div className="col-12 lead py-3 text-center h-20">Channels</div>
            <div
              className="col-12 my-1 h-60 px-3"
              style={{ overflowY: "scroll", overflowX: "hidden" }}
            >
              {channels.map((x, i) => {
                return (
                  <div
                    key={`channel-${i}`}
                    className="row no-gutters justify-content-between h-33 flex-nowrap"
                  >
                    <div className="col-6 lead">{x.name}</div>
                    <div
                      className="col-auto py-2 px-3"
                      style={{ minWidth: "125px" }}
                    >
                      <div
                        className="pnl concave-2"
                        style={{
                          justifyContent: x.botEnabled
                            ? "flex-end"
                            : "flex-start",
                        }}
                      >
                        <div
                          onMouseDown={(e) => onMD(e, x.name)}
                          onMouseUp={(e) => onMU(e, x.name, setMove)}
                          onMouseMove={(e) => onMM(e, x.name, setMove)}
                          onMouseLeave={() => onML(setMove)}
                          onTouchStart={(e) => handleTouchStart(e, x.name)}
                          onTouchMove={(e) =>
                            handleTouchMove(e, x.name, setMove)
                          }
                          onTouchEnd={(e) => handleTouchEnd(e, x.name, setMove)}
                          className="pnl w-65 convex-2"
                          style={{
                            background: "rgb(165, 250, 251)",

                            transform: `translateX(${
                              move[0] === x.name ? move[1] : 0
                            }px)`,
                          }}
                        ></div>
                      </div>
                    </div>
                  </div>
                );
              })}
            </div>
          </div>
        </div>

        <div
          className="col-lg-3 col-md-4 col-sm-4 col-8"
          style={{ height: "300px", maxWidth: "265px" }}
        >
          <div className="row no-gutters h-100 p-2">
            <div className="col-md-12 h-30 p-2">
              <div className="newish convex-1 pnl align-items-center justify-content-center lead">
                Prefix
              </div>
            </div>
            <div className="col-md-12 h-70 p-2">
              <div className="concave-1 pnl align-items-center justify-content-center display-4 newish">
                <FaPen
                  onClick={(e) => {
                    setEdit(true);
                    e.currentTarget.nextSibling.nextSibling.focus();
                  }}
                  style={{
                    position: "absolute",
                    top: "30px",
                    right: "30px",
                    fontSize: "20px",
                    color: "white",
                    display: edit ? "none" : "block",
                  }}
                ></FaPen>
                <FaCheck
                  onClick={() => setEdit(false)}
                  style={{
                    position: "absolute",
                    top: "30px",
                    right: "30px",
                    fontSize: "20px",
                    color: "white",
                    display: edit ? "block" : "none",
                  }}
                ></FaCheck>

                <input
                  onBlur={() => setEdit(false)}
                  onFocus={() => setEdit(true)}
                  className="prfx-inpt selectDisable"
                  type="text"
                  value={prefix}
                  onChange={(e) => setPrefix(e.target.value)}
                ></input>
              </div>
            </div>
          </div>
        </div>

        {/* KOMANDU SARASAS */}

        <div className="col-lg-5 col-sm-12 col-12 p-2">
          <div className="pnl convex-1 row px-4 py-2 no-gutters justify-content-between newish h-100">
            <div className="col-12 lead py-3 text-center h-20">
              Commands Status
            </div>
            <div
              className="col-12 my-1 h-60 px-3"
              style={{ overflowY: "scroll", overflowX: "hidden" }}
            >
              <div className="row no-gutters">
                {commands.map((x, i) => {
                  return (
                    <div key={`channel-${i}`} className="col-6 ">
                      <div className="row no-gutters">
                        <div className="col-7" style={{ fontSize: "20px" }}>
                          {x.name}
                        </div>
                        <div
                          className="col-auto py-1 px-3"
                          style={{ minWidth: "85px" }}
                        >
                          <div
                            className="pnl concave-2"
                            style={{
                              justifyContent: x.botEnabled
                                ? "flex-end"
                                : "flex-start",
                            }}
                          >
                            <div
                              onMouseDown={(e) => onMD(e, x.name)}
                              onMouseUp={(e) => onMU(e, x.name, setMove)}
                              onMouseMove={(e) => onMM(e, x.name, setMove)}
                              onMouseLeave={() => onML(setMove)}
                              onTouchStart={(e) => handleTouchStart(e, x.name)}
                              onTouchMove={(e) =>
                                handleTouchMove(e, x.name, setMove)
                              }
                              onTouchEnd={(e) =>
                                handleTouchEnd(e, x.name, setMove)
                              }
                              className="pnl w-65 convex-2"
                              style={{
                                background: "rgb(165, 250, 251)",

                                transform: `translateX(${
                                  move[0] === x.name ? move[1] : 0
                                }px)`,
                              }}
                            ></div>
                          </div>
                        </div>
                      </div>
                    </div>
                  );
                })}
              </div>
            </div>
          </div>
        </div>

        {/* KOMANDU SARASAS */}
      </div>
      <div className="row no-gutters">
        <div
          className="col-12 col-md-6 pl-2 pr-3 pt-3"
          style={{ height: "300px" }}
        >
          <div className="pnl convex-1 newish">
            <Chart
              data={{
                title: "Top Emojis",
                units: "times",
                items: [
                  { image: emojis[0] },
                  { image: emojis[1] },
                  { image: emojis[2] },
                  { image: emojis[3] },
                ],
              }}
            ></Chart>
          </div>
        </div>
        <div
          className="col-12 col-md-6 pl-3 pr-2 pt-3"
          style={{ height: "300px" }}
        >
          <div className="pnl convex-1 newish">
            <Chart
              data={{
                title: "Top Commands",
                units: "times",
                items: [
                  { name: "!mute" },
                  { name: "!ban" },
                  { name: "!kick" },
                  { name: "!role" },
                ],
              }}
            ></Chart>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
