import React from "react";
import { Scrollbars } from "react-custom-scrollbars";
import { FaPen, FaTrash, FaCheck, FaPlus } from "react-icons/fa";
import { useState, useEffect } from "react";
import { getLongestWidth } from "./events";
const AutoMessages = () => {
  const allEvents = ["Welcome", "Goodbye", "Level", "Role"];
  const [longestEvent, setLongestEvent] = useState(0);
  const [autoroles, setAutoroles] = useState([
    {
      on: { current: "Welcome" },
      message: { current: "Hello, {USER}, welcome to our channel" },
    },
    {
      on: { current: "Goodbye" },
      message: { current: "Bye, {USER}, we will miss you" },
    },
    {
      on: { current: "Level" },
      message: { current: "{USER} has just leveled up to {LEVEL}" },
    },
  ]);

  const [editRoles, setEditRoles] = useState({
    id: -1,
    show: false,
    type: "wait",
  });

  useEffect(() => {
    let longest = getLongestWidth(autoroles.map((x) => x.on.current));
    setLongestEvent(longest);
    if (
      autoroles.length &&
      autoroles[autoroles.length - 1].on.current === "select"
    ) {
      setEditRoles((edit) =>
        Object.assign({}, edit, {
          type: "edit",
          id: autoroles.length - 1,
          show: true,
        })
      );
    }
  }, [autoroles]);
  return (
    <div className="w-100 py-4 px-4" style={{ position: "relative" }}>
      <div className="lead justify-content-center mb-3 d-flex">
        Auto Messages
      </div>
      <div
        onMouseDown={(e) => {
          e.currentTarget.classList.add("convex-pressed");
          e.currentTarget.classList.remove("convex-2");
          setAutoroles((roles) => {
            let arr = [...roles];
            arr[arr.length] = {
              on: { current: "select" },
              role: { current: "select" },
            };
            return arr;
          });
        }}
        onMouseUp={(e) => {
          e.currentTarget.classList.remove("convex-pressed");
          e.currentTarget.classList.add("convex-2");
        }}
        style={{
          position: "absolute",
          top: "30px",
          right: "30px",
          width: "30px",
          height: "30px",
          borderRadius: "50%",
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
          cursor: "pointer",
        }}
        className="convex-2 ml-4 select-disable"
      >
        <FaPlus fontSize="18"></FaPlus>
      </div>
      <Scrollbars
        hideTracksWhenNotNeeded
        className="w-100 h-77"
        renderTrackHorizontal={(props) => (
          <div {...props} className="track-horizontal" />
        )}
        renderTrackVertical={(props) => (
          <div {...props} className="track-vertical" />
        )}
        renderThumbHorizontal={(props) => (
          <div {...props} className="thumb-horizontal" />
        )}
        renderThumbVertical={(props) => (
          <div {...props} className="thumb-vertical" />
        )}
        renderView={(props) => <div {...props} className="view" />}
      >
        {editRoles.type === "wait" ? (
          <table
            className="table borderless w-100 lead"
            style={{ color: "white" }}
          >
            <tbody>
              <tr style={{ borderBottom: "1px solid white" }}>
                <td style={{ padding: "0px" }} scope="col">
                  Event
                </td>
                <td style={{ padding: "0px" }} scope="col">
                  Message
                </td>
                {/* <td style={{ padding: "0px" }} scope="col">
                  Edit
                </td> */}
              </tr>
              {autoroles.map((x, i) => {
                return (
                  <tr
                    className="choice"
                    onClick={() =>
                      setEditRoles((edit) =>
                        Object.assign({}, edit, {
                          id: i,
                          show: true,
                          type: "edit",
                        })
                      )
                    }
                  >
                    {Object.keys(x).map((key) => {
                      return (
                        <td style={{ padding: "7px" }}>
                          {key !== "edit" ? (
                            x[key].current.length > 8 ? (
                              x[key].current.substring(0, 8) + "..."
                            ) : (
                              x[key].current
                            )
                          ) : (
                            <div>
                              <FaPen
                                fontSize="18"
                                onClick={(e) => {
                                  e.stopPropagation();
                                  setEditRoles((edit) =>
                                    Object.assign({}, edit, {
                                      id: i,
                                      type: "edit",
                                    })
                                  );
                                }}
                              ></FaPen>
                              <FaTrash
                                fontSize="18"
                                onClick={(e) => {
                                  e.stopPropagation();
                                  setAutoroles((autoroles) => {
                                    let arr = [...autoroles];
                                    arr.splice(i, 1);
                                    return arr;
                                  });
                                }}
                              ></FaTrash>
                            </div>
                          )}
                        </td>
                      );
                    })}
                  </tr>
                );
              })}
            </tbody>
          </table>
        ) : editRoles.type === "edit" ? (
          <table
            className="table borderless w-100 lead"
            style={{ color: "white" }}
          >
            <tbody>
              <tr style={{ borderBottom: "1px solid white" }}>
                <td scope="col" style={{ padding: 0 }}>
                  Event
                </td>
                <td scope="col" style={{ padding: 0 }}>
                  Message
                </td>
                <td scope="col" style={{ padding: 0 }}>
                  Edit
                </td>
              </tr>
              <tr>
                {Object.keys(
                  Object.assign({}, autoroles[editRoles.id], {
                    save: { current: "Save" },
                  })
                ).map((key) => {
                  return (
                    <td style={{ padding: 0 }}>
                      {key === "save" ? (
                        <FaCheck
                          onClick={() => {
                            setEditRoles((edit) =>
                              Object.assign({}, edit, { type: "wait" })
                            );
                          }}
                        ></FaCheck>
                      ) : autoroles[editRoles.id][key].current.length > 8 ? (
                        autoroles[editRoles.id][key].current.substring(0, 8) +
                        "..."
                      ) : (
                        autoroles[editRoles.id][key].current
                      )}
                    </td>
                  );
                })}
              </tr>
              <tr style={{ borderRadius: "25px", overflow: "hidden" }}>
                <td style={{ height: "104px", padding: "5px" }}>
                  <Scrollbars
                    style={{ width: `${longestEvent + 35}px` }}
                    className="concave-2 rounded"
                    hideTracksWhenNotNeeded
                    renderTrackHorizontal={(props) => (
                      <div {...props} className="track-horizontal" />
                    )}
                    renderTrackVertical={(props) => (
                      <div {...props} className="track-vertical" />
                    )}
                    renderThumbHorizontal={(props) => (
                      <div {...props} className="thumb-horizontal" />
                    )}
                    renderThumbVertical={(props) => (
                      <div {...props} className="thumb-vertical" />
                    )}
                    renderView={(props) => <div {...props} className="view" />}
                  >
                    {allEvents.map((ev) => {
                      return (
                        <div
                          className="choice"
                          onClick={() => {
                            setAutoroles((roles) => {
                              console.log(roles);
                              let arr = [...roles];
                              arr[editRoles.id].on.current = ev;
                              return arr;
                            });
                          }}
                        >
                          {ev}
                        </div>
                      );
                    })}
                  </Scrollbars>
                </td>
                <td style={{ height: "104px", padding: "5px" }}>
                  <textarea
                    type="text"
                    className="auto-msg-txtar w-100 h-100 concave-2 rounded"
                  ></textarea>
                </td>
              </tr>
            </tbody>
          </table>
        ) : (
          <div></div>
        )}
      </Scrollbars>
    </div>
  );
};

export default AutoMessages;
