import React, { useRef, useEffect } from "react";
import { users } from "../../../data/data";
import Level from "./level";
import PerfectScrollbar from "react-perfect-scrollbar";
const Scoreboard = () => {
  const scrollbar = useRef(null);
  useEffect(() => {
    scrollbar.current.updateScroll();
  }, []);
  return (
    <div className="w-100 h-100 pt-4 px-4">
      <div className="lead text-center ">Leaderboard</div>
      <PerfectScrollbar
        ref={scrollbar}
        options={{ wheelSpeed: 0.4, wheelPropagation: false }}
        className="w-100 h-85 px-3"
      >
        <table className="table borderless w-100 lead">
          <tbody>
            {users
              .sort((a, b) => (a.exp > b.exp ? -1 : a.exp < b.exp ? 1 : 0))
              .map((x, i) => (
                <tr className="scrbrd-row">
                  <td className="align-middle text-center">
                    <div
                      style={{
                        width: "30px",
                        height: "30px",
                        borderRadius: "50%",
                        background:
                          i + 1 === 1
                            ? "gold"
                            : i + 1 === 2
                            ? "silver"
                            : i + 1 === 3
                            ? "#cd7f32"
                            : "gray",
                        display: "flex",
                        alignItems: "center",
                        justifyContent: "center",
                      }}
                    >
                      {i + 1}
                    </div>
                  </td>
                  <td className="align-middle text-center">
                    <div style={{ display: "flex", alignItems: "center" }}>
                      <div
                        style={{
                          width: "50px",
                          height: "50px",
                          marginRight: "10px",
                        }}
                      >
                        <img
                          src={x.image}
                          alt={x.image}
                          className="img-fluid"
                        ></img>
                      </div>
                      <div>{x.name}</div>
                      <div> Exp:{x.exp}</div>
                    </div>
                  </td>
                  <td className="align-middle text-center"></td>
                  <td className="align-middle d-flex justify-content-center">
                    <Level level={x.level} exp={x.exp}></Level>
                  </td>
                </tr>
              ))}
          </tbody>
        </table>
      </PerfectScrollbar>
    </div>
  );
};

export default Scoreboard;
