import React from "react";
import { Scrollbars } from "react-custom-scrollbars";
import { FaPen, FaTrash } from "react-icons/fa";
import { useState } from "react";
const AutoRoles = () => {
  const allRoles = ["Guest", "Fan", "Memeber", "Admin"];
  const allEvents = ["Join", "Level X", "Level XX"];
  const autoroles = [
    {
      id: 1,
      on: { current: "Join", choice: allEvents },
      role: { current: "Guest", choice: allRoles },
    },
    {
      id: 2,
      on: { current: "Level 3", choice: allEvents },
      role: { current: "Fan", choice: allRoles },
    },
    {
      id: 3,
      on: { current: "Level 10", choice: allEvents },
      role: { current: "Member", choice: allRoles },
    },
  ];

  const [editBox, setEditBox] = useState({});

  return (
    <div className="w-100 py-4 px-4" style={{ position: "relative" }}>
      <div className="lead justify-content-center mb-3 d-flex">
        Auto Roles
        <div
          onMouseDown={(e) => {
            e.currentTarget.classList.add("convex-pressed");
            e.currentTarget.classList.remove("convex-2");
          }}
          onMouseUp={(e) => {
            e.currentTarget.classList.remove("convex-pressed");
            e.currentTarget.classList.add("convex-2");
          }}
          style={{
            width: "30px",
            height: "30px",
            borderRadius: "50%",
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
            cursor: "pointer",
          }}
          className="convex-2 ml-4 select-disable"
        >
          +
        </div>
      </div>
      <Scrollbars
        hideTracksWhenNotNeeded
        className="w-100 h-75"
        renderTrackHorizontal={(props) => (
          <div {...props} className="track-horizontal" />
        )}
        renderTrackVertical={(props) => (
          <div {...props} className="track-vertical" />
        )}
        renderThumbHorizontal={(props) => (
          <div {...props} className="thumb-horizontal" />
        )}
        renderThumbVertical={(props) => (
          <div {...props} className="thumb-vertical" />
        )}
        renderView={(props) => <div {...props} className="view" />}
      >
        <table
          className="table borderless w-100 lead"
          style={{ color: "white" }}
        >
          <thead>
            <tr>
              <th scope="col">Event</th>
              <th scope="col">Role</th>
              <th scope="col">Edit</th>
            </tr>
          </thead>

          <tbody>
            {autoroles.map((x) => {
              return (
                <tr>
                  {Object.keys(Object.assign({}, x, { edit: true })).map(
                    (key) => {
                      return (
                        <td>
                          {key !== "edit" ? (
                            x[key].current
                          ) : (
                            <div>
                              <FaPen fontSize="18"></FaPen>
                              <FaTrash fontSize="18"></FaTrash>
                            </div>
                          )}
                        </td>
                      );
                    }
                  )}
                </tr>
              );
            })}
          </tbody>
        </table>
      </Scrollbars>
    </div>
  );
};

export default AutoRoles;
