import React from "react";

const Chart = ({ data }) => {
  return (
    <div className="container-fluid">
      <div className="row">{data.title}</div>
      <div className="row">
        {" "}
        <div className="col-2">
          {data.items.map((x) => {
            return (
              <div className={`h-18 d-flex`}>
                {x.image ? (
                  <div style={{ width: "30px", height: "30px" }}>
                    <img src={x.image} className="img-fluid"></img>
                  </div>
                ) : (
                  <div className="lead">{x.name}</div>
                )}
              </div>
            );
          })}
        </div>
        <div className="col-10">
          {data.items.map((x, i) => {
            return (
              <div className={`h-18 d-flex`}>
                <div
                  className={`w-${(i + 1) * 20}`}
                  style={{
                    paddingBottom: "0px",
                    borderRight:
                      i === data.items.length - 1 ? "2px solid white" : "none",
                  }}
                >
                  <div className={`w-100 bg-primary h-100`}></div>
                </div>
              </div>
            );
          })}
          <div style={{ borderTop: "2px solid white" }} className="w-100">
            {data.units}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Chart;
