import React, { useState } from "react";
import { Spring, animated, config } from "react-spring/renderprops";
import paths from "./paths";
import { interpolate } from "flubber";
const interpolator = interpolate(paths["discord"], paths["youtube"]);
const Animation = () => {
  const [pinger, ping] = useState(true);

  return (
    <svg width="500" height="500" viewBox="0 0 500 500">
      <Spring
        config={{ delay: 1000 }}
        reset
        native
        from={{ t: pinger ? 0 : 1, color: pinger ? "#7289da" : "red" }}
        to={{ t: pinger ? 1 : 0, color: pinger ? "red" : "#7289da" }}
        onRest={() => ping(!pinger)}
      >
        {({ t, color }) => (
          <animated.path d={t.interpolate(interpolator)} fill={"WHITE"} />
        )}
      </Spring>
    </svg>
  );
};

export default Animation;
