import React from "react";

const DropDown = ({ items, current, show }) => {
  return (
    <div style={{ position: "relative", height: "100%" }}>
      {current}
      <div style={{ position: "absolute", display: show ? "block" : "none" }}>
        {items.map((x) => {
          return <div>{x}</div>;
        })}
      </div>
    </div>
  );
};

export default DropDown;
